package com.stringmethods;

public class Example4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/** contains(CharSequence s):
			
		**Returns true if and only if this string contains the 
			 * specified sequence of char values.
			 * 
			 */
			
			String text = "I LOVE BIRIYANI";
			
			System.out.println(text.contains("LOVE"));
			System.out.println(text.contains("i"));
			System.out.println(text.contains("BIRIYANI"));
			
			


	}

}
