package com.stringmethods;

public class Example5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/**
		 * endsWith(String suffix):
		 * 
		 * Tests if this string ends with the specified suffix.
		 */
		
		String text = "Practice makes man perfect";
		
		System.out.println(text.endsWith("Practice"));
		
		System.out.println(text.endsWith("perfect"));

		System.out.println(text.endsWith("man perfect"));
		
		System.out.println(text.endsWith("makes"));
		
	}



	}


